INSERT INTO `tbl_term_conditions` (`id`, `heading`, `content`, `create_at`) VALUES
(1, 'Terms  & Conditions ', '<p class=\"MsoNormal\" align=\"center\" style=\"margin-bottom:0in;margin-bottom:.0001pt;\r\ntext-align:center;line-height:normal\"><b><span style=\"font-family:&quot;Verdana&quot;,&quot;sans-serif&quot;\">Terms\r\nand Conditions<o:p></o:p></span></b></p>\r\n\r\n<p class=\"MsoNormal\" style=\"margin-bottom:0in;margin-bottom:.0001pt;text-align:\r\njustify;line-height:normal\"><span style=\"font-family:&quot;Verdana&quot;,&quot;sans-serif&quot;\">&nbsp;</span></p>\r\n\r\n<p class=\"MsoNormal\" style=\"margin-bottom:0in;margin-bottom:.0001pt;text-align:\r\njustify;line-height:normal\"><span style=\"font-family:&quot;Verdana&quot;,&quot;sans-serif&quot;\">Welcome\r\nto Jaipur Window, an online platform for discovering and purchasing unique\r\nhandcrafted products from India. By accessing or using our website, you agree\r\nto be bound by these terms and conditions and our privacy policy.<o:p></o:p></span></p>\r\n\r\n<p class=\"MsoNormal\" style=\"margin-bottom:0in;margin-bottom:.0001pt;text-align:\r\njustify;line-height:normal\"><span style=\"font-family:&quot;Verdana&quot;,&quot;sans-serif&quot;\">&nbsp;</span></p>\r\n\r\n<p class=\"MsoNormal\" style=\"margin-bottom:0in;margin-bottom:.0001pt;text-align:\r\njustify;line-height:normal\"><span style=\"font-family:&quot;Verdana&quot;,&quot;sans-serif&quot;\">1.\r\n<b>About Jaipur Window</b><o:p></o:p></span></p>\r\n\r\n<p class=\"MsoNormal\" style=\"margin-bottom:0in;margin-bottom:.0001pt;text-align:\r\njustify;line-height:normal\"><span style=\"font-family:&quot;Verdana&quot;,&quot;sans-serif&quot;\">&nbsp;</span></p>\r\n\r\n<p class=\"MsoNormal\" style=\"margin-top:0in;margin-right:0in;margin-bottom:0in;\r\nmargin-left:.5in;margin-bottom:.0001pt;text-align:justify;line-height:normal\"><span style=\"font-family:&quot;Verdana&quot;,&quot;sans-serif&quot;\">Jaipur Window is an e-commerce\r\nplatform that showcases and sells a variety of handmade and handicraft products\r\nsourced from local artisans and small businesses in India. Our mission is to\r\npromote and preserve the rich cultural heritage and traditional craftsmanship\r\nof the country.<o:p></o:p></span></p>\r\n\r\n<p class=\"MsoNormal\" style=\"margin-bottom:0in;margin-bottom:.0001pt;text-align:\r\njustify;line-height:normal\"><span style=\"font-family:&quot;Verdana&quot;,&quot;sans-serif&quot;\">&nbsp;</span></p>\r\n\r\n<p class=\"MsoNormal\" style=\"margin-bottom:0in;margin-bottom:.0001pt;text-align:\r\njustify;line-height:normal\"><span style=\"font-family:&quot;Verdana&quot;,&quot;sans-serif&quot;\">2.\r\n<b>Product Information<o:p></o:p></b></span></p>\r\n\r\n<p class=\"MsoNormal\" style=\"margin-bottom:0in;margin-bottom:.0001pt;text-align:\r\njustify;line-height:normal\"><span style=\"font-family:&quot;Verdana&quot;,&quot;sans-serif&quot;\">&nbsp;</span></p>\r\n\r\n<p class=\"MsoNormal\" style=\"margin-top:0in;margin-right:0in;margin-bottom:0in;\r\nmargin-left:.5in;margin-bottom:.0001pt;text-align:justify;line-height:normal\"><span style=\"font-family:&quot;Verdana&quot;,&quot;sans-serif&quot;\">All product information, including\r\ndescriptions, images, and pricing, is provided to the best of our knowledge.\r\nHowever, we cannot guarantee the accuracy or completeness of such information.\r\nProduct availability, colors, and specifications are subject to change without\r\nnotice.<o:p></o:p></span></p>\r\n\r\n<p class=\"MsoNormal\" style=\"margin-bottom:0in;margin-bottom:.0001pt;text-align:\r\njustify;line-height:normal\"><span style=\"font-family:&quot;Verdana&quot;,&quot;sans-serif&quot;\">&nbsp;</span></p>\r\n\r\n<p class=\"MsoNormal\" style=\"margin-bottom:0in;margin-bottom:.0001pt;text-align:\r\njustify;line-height:normal\"><span style=\"font-family:&quot;Verdana&quot;,&quot;sans-serif&quot;\">3.\r\n<b>Ordering and Delivery</b><o:p></o:p></span></p>\r\n\r\n<p class=\"MsoNormal\" style=\"margin-bottom:0in;margin-bottom:.0001pt;text-align:\r\njustify;line-height:normal\"><span style=\"font-family:&quot;Verdana&quot;,&quot;sans-serif&quot;\">&nbsp;</span></p>\r\n\r\n<p class=\"MsoListParagraphCxSpFirst\" style=\"margin-top:0in;margin-right:0in;\r\nmargin-bottom:0in;margin-left:.75in;margin-bottom:.0001pt;mso-add-space:auto;\r\ntext-align:justify;text-indent:-.25in;line-height:normal;mso-list:l0 level1 lfo1\"><!--[if !supportLists]--><span style=\"font-family:&quot;Verdana&quot;,&quot;sans-serif&quot;;mso-fareast-font-family:Verdana;\r\nmso-bidi-font-family:Verdana\">a.<span style=\"font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 7pt; line-height: normal; font-family: &quot;Times New Roman&quot;;\">&nbsp;&nbsp; </span></span><!--[endif]--><b><span style=\"font-family:&quot;Verdana&quot;,&quot;sans-serif&quot;\">Placing an Order</span></b><span style=\"font-family:&quot;Verdana&quot;,&quot;sans-serif&quot;\">: You can place an order on our\r\nwebsite by selecting the desired products and completing the checkout process.\r\nWe accept various payment methods, including credit/debit cards, digital\r\nwallets, and bank transfers.<o:p></o:p></span></p>\r\n\r\n<p class=\"MsoListParagraphCxSpMiddle\" style=\"margin-top:0in;margin-right:0in;\r\nmargin-bottom:0in;margin-left:.75in;margin-bottom:.0001pt;mso-add-space:auto;\r\ntext-align:justify;line-height:normal\"><span style=\"font-family:&quot;Verdana&quot;,&quot;sans-serif&quot;\">&nbsp;</span></p>\r\n\r\n<p class=\"MsoListParagraphCxSpLast\" style=\"margin-top:0in;margin-right:0in;\r\nmargin-bottom:0in;margin-left:.75in;margin-bottom:.0001pt;mso-add-space:auto;\r\ntext-align:justify;text-indent:-.25in;line-height:normal;mso-list:l0 level1 lfo1\"><!--[if !supportLists]--><span style=\"font-family:&quot;Verdana&quot;,&quot;sans-serif&quot;;mso-fareast-font-family:Verdana;\r\nmso-bidi-font-family:Verdana\">b.<span style=\"font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 7pt; line-height: normal; font-family: &quot;Time